  const fetchQuiz = async () => {
    const res = await fetch("https://opentdb.com/api.php?amount=5&category=18&difficulty=easy&type=multiple");
    const { results } = await res.json();
    setQuizzes(results);
  }

  return (
    <>
      <button onClick={fetchQuiz}>Start Quiz</button>
